import React, { Component } from 'react';
import './ListItem.css';
 
class ListItem extends Component{
    constructor(props){
        super(props);
    }
    render(){
        const item=this.props.item;
        if(item.done){
            return(
                <div>
                    <p className="done-item">{item.content}</p>
                    <button>make it false</button>
                </div>
            );
        }else{
            return(
                <div>
                    <p className="item">{item.content}</p>
                    <button>make it true</button>
                </div>
            );
        }
    }
}

export default ListItem;
