import React, { Component } from 'react';
import ListItem from './ListItem';
import NewItem from './NewItem';

class TodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            todoList:[
                {content:'React practice',done:true},
                {content:'game time',done:false}
              ]
        }
    }

    addNewItem=(newItemContent)=>{
        const newList=[...this.state.todoList,{content:newItemContent,done:false}]
        this.setState({
            todoList:newList
        })
    }

    changeItem=(item)=>{
        const ii=this.state.todoList.indexOf(item.content);
        const newtodoList=this.state.todoList;
        newtodoList[ii].done=true;
        this.setState({
            toduList:newtodoList
        })
    }
    

    render(){
        return(
            <div>
                {
                    this.state.todoList.map(item=>(
                        <div>
                            <ListItem item={item}/>
                            <button onClick={this.changeItem}>make it true</button>
                        </div>
                    ))
                }
                <NewItem addItem={this.addNewItem}/>
            </div>
        );
    }
}

export default TodoList;
