import React, { Component,useState, Fragment, useEffect } from 'react';
import ListItem from './ListItem';
import NewItem from './NewItem';
import { getTodos, addTodo, deleteTodo, updateTodo } from "./api/TodoApi";

class TodoList extends Component{
    constructor(props){
        super(props);
        this.state={
            todoList:[
                {content:'React practice',done:true},
                {content:'game time',done:false}
              ]
        }
    }
    addNewItem=(newItemContent)=>{
        const newList=[...this.state.todoList,{content:newItemContent,done:false}]
        this.setState({
            todoList:newList
        })
    }
    render(){
        const [list, setList] = useState(null);
        const [error, setError] = useState("");
        handleLoadTasks = () => {
          getTodos()
            .then((response) => {
              setList(response);
            })
            .catch((error) => {
              setError("Unable to retrieve todo's");
            });
        };
      
      handleAddTask = (inputV) => {
          if (inputV === "") return;
      
          const newTask = {
            id: _.parseInt(list.length ? list[list.length - 1].id : 0) + 1,
            content: inputV,
          };
      
          addTodo(newTask).then(() => {
            setList([...list, newTask]);
            setInputValue("");
          });
        };
      
      handleDeleteTask = (id) =>
          deleteTodo(id).then(() => {
            setList(list.filter((item) => item.id !== id));
          });
      
      handleUpdateTask = (task) => {
          if (task.content === "") return;
      
          updateTodo(task).then((response) => {
            setList(
              list.map((x) => (x.id === response.id ? { ...x, ...response } : x))
            );
          });
        };
        useEffect(() => {
          handleLoadTasks();
        }, []);
      
        if (list === null) {
          return <div>Tasks is loading ...</div>;
        }
      
        if (error) {
          return <div>{error}</div>;
        }
      
        return(
            
            <div>
                {
                    this.state.todoList.map(item=>(
                        <div>
                            <ListItem item={item}/>
                        </div>
                    ))
                }
                <NewItem addItem={this.handleAddTask}/>
            </div>
        );
    }
}

export default TodoList;
